import { StyleSheet } from "react-native";

export const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  cardCustom: {
    width: "50%",
  },

  title: {
    marginTop: 3,
    fontSize: 16,
    color: "gray",
    textAlign: "center",
    fontWeight: "bold",
  },

  photo: {
    resizeMode: "contain",
    width: "100%",
    aspectRatio: 3.5 / 4.5,
    alignSelf: "center",
  },

  category: {
    fontSize: 14,
    fontWeight: "bold",
    margin: 4,
    textAlign: "center",
    color: "#2cd18a",
  },
});

export default styles;
