import { StyleSheet } from "react-native";

export const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  card: {
    width: "50%",
    height: 250,
    backgroundColor: "red",
    borderColor: "gray",
    borderBottomWidth: 2,
  },

  title: {},

  photo: {
    width: "100%",
    height: 550,
    alignSelf: "center",
  },

  category: {
    fontSize: 14,
    fontWeight: "bold",
    margin: 10,
    color: "#2cd18a",
  },
});

export default styles;
