import { StyleSheet } from "react-native";

export const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },

  appTitle: {
    fontSize: 16,
    fontWeight: "700",
    margin: 5,
    textAlign: "center",
    borderBottomColor: "gray",
    width: "46%",
    borderBottomWidth: 2,
  },

  cardRaw: {
    flexDirection: "row",
    flexWrap: "wrap",
    alignItems: "center",
    justifyContent: "center",
  },

  cardCustom: {
    width: "48%",
    padding: 10,
    margin: "1%",
  },

  title: {
    marginTop: 3,
    fontSize: 16,
    color: "gray",
    textAlign: "center",
    fontWeight: "bold",
  },

  photo: {
    resizeMode: "contain",
    width: "100%",
    aspectRatio: 3 / 4.5,
    alignSelf: "center",
  },

  category: {
    fontSize: 14,
    fontWeight: "bold",
    margin: 4,
    textAlign: "center",
    color: "#2cd18a",
  },
});

export default styles;
