import React, { useState, useContext } from "react";
import { Text, TextInput, View, Button } from "react-native";
import { styles } from "./styles";
import { db } from "./firebase-config";
import { collection, addDoc } from "firebase/firestore";
import { Alert } from "react-native-web";

export const AddTransaction = () => {
  const [text, setText] = useState("");
  const [amount, setAmount] = useState("");

  const userTransactionTable = collection(db, "userTransactionTable");

  const handleSubmit = () => {
    addDoc(userTransactionTable, {
      userID: 1,
      text: text,
      amount: amount,
    });
    Alert.alert("your info add successfully!");
    setText("");
    setAmount("");
  };

  return (
    <>
      <Text style={[styles.sectionTitle, styles.h3]}>Add new transaction</Text>
      <View>
        <View>
          <Text style={styles.label}>Text</Text>
          <TextInput
            style={[styles.input]}
            onChangeText={(value) => setText(value)}
            value={text}
            placeholder="Enter text..."
          />
        </View>
        <View>
          <Text style={styles.label}>
            amount{"\n"}(negative - expense, positive - income)
          </Text>
          <TextInput
            style={[styles.input]}
            keyboardType="numeric"
            onChangeText={(value) => setAmount(value)}
            value={amount}
            placeholder="Enter amount..."
          />
          <Button
            onPress={handleSubmit}
            title="Add Transaction"
            color="#444"
            accessibilityLabel="Learn more about this purple button"
          />
        </View>
      </View>
    </>
  );
};
